CREATE TABLE projet4 (
  id INT PRIMARY KEY AUTO_INCREMENT,
  `Projet` VARCHAR(255),
  `Type` VARCHAR(255),
  `Client` VARCHAR(255),
  `Statut` VARCHAR(255),
  `Début` VARCHAR(255),
  `Fin_prévue` VARCHAR(255),
  `Avancement_(%)` VARCHAR(255) COMMENT 'Avancement_(%)',
  `Priorité` VARCHAR(255),
  `Paiement_(FCFA)` VARCHAR(255) COMMENT 'Paiement_(FCFA)',
  `Notes` VARCHAR(255)
);

